        -- line code commend
        ''--'' lcc,
        -- bounds of a (t)sql word
        ''s''''"_*+/,.;:\<>()=Â¬''+char(13)+char(10)+'' ''+char(9) bounds,
        ''%[^a-z0-9_]%'' non_word,
