    if left(@name,1)<>''['' and right(@name,1)<>'']''
        begin
        if charindex('' '',@name)>0 or dbo.fn__token_sql(@name)=1
            set @name=quotename(@name)
        end
    if @i>1 set @r=@r+@seps
    set @r=@r+@name
