    end

select @comment=dbo.fn__printf(@comment,@p1,@p2,@p3,@p4,null,null,null,null,null,null)
select @line=replicate(''='',76)
select @line=''-- =''+replicate(''='',72-len(@comment))+'' ''+@comment+'' ==''
return @line

end -- fn__prints'

