    @separator nvarchar(50),
    @part int
) returns nvarchar(4000)
as
begin

    if @part < 0 goto ret

    declare @index int
    declare @spart nvarchar(4000)
    set @index = 0

    while len(@s) > 0
    begin

        if charindex(@separator,@s) = 0
        begin
            if @index = @part
            begin
                set @spart = @s
                goto ret
            end

            set @spart = NULL
            goto ret
        end


        select
            @spart = substring(@s,0,charindex(@separator,@s)),
            @s = substring(@s,charindex(@separator,@s)+1,len(@s))

        if @index = @part
            goto ret
        else
            set @index = @index+1
    end

ret:
    return @spart
end'

end try
