create function [dbo].[fn__t9_vkey](@string nvarchar(4000),@max_len tinyint=9)
returns nvarchar(32)
as
begin
declare @c nvarchar(1)
declare @i int set @i=1
declare @r nvarchar(32) set @r=''''
