exec sp_executesql N'
    select
        @ver=cast(val1 as decimal(10,4)),
        @aut=val2
    from dbo.fn__script_info(@obj,@typ,0)
    ',N'@obj sysname,@typ char(2),@ver numeric(10,4) out,@aut sysname out',
    @obj='fn__parse_url',@typ='rv',@ver=@ver out,@aut=@aut out
if not @ver is null
