    if @ver=130906
        begin
        if @aut!='s.zaglio'
            raiserror('local "%s.%s" with same version but different author',
                      16,1,@db,'sp__file_get') with nowait
        raiserror('skipped "%s.%s" because local is the same',
                  10,1,@db,'sp__file_get') with nowait
        goto skip_sp__file_get
        end
