exec @ret=sp__err ''ole error (%s;%s;%s)'',@proc,@p1=@cmd,@p2=@source,@p3=@description
goto ret

-- ===================================================================== exit ==
ret:
if @adodbstream!=0 exec @hr = sp_oadestroy @adodbstream
set nocount off
return @ret
end -- [sp__file_read_blob]'

