goto ret

err_wpro:   exec @ret=sp__err ''worked protection against hackers :-) '',@proc goto ret
err_ptr:    exec @ret=sp__err ''ptr get error'',@proc goto ret

ret:
if @adodbstream!=0 exec @hr = sp_oadestroy @adodbstream
set nocount off
return @ret
end -- [sp__file_read_stream]'

