insert #sql select ''jsteps'',''
use [%db%]
insert #tmp_found(
    db,id,colid,obj,xtype,[txt1_or_obj],p1,p2,p3
    )
select
    @db,j.job_id,s.step_id,
    j.name,
    ''''job.step'''',
    left(s.step_name,128),
    patindex(@m1,replace(s.step_name,'''']'''',''''Â¬'''')),
    patindex(@m2,replace(s.step_name,'''']'''',''''Â¬'''')),
    patindex(@m3,replace(s.step_name,'''']'''',''''Â¬''''))
from msdb..sysjobsteps s with (nolock)
join msdb..sysjobs j with (nolock)
on s.job_id=j.job_id
where database_name=@db
and (
       patindex(@m1,replace(s.step_name,'''']'''',''''Â¬''''))>0
    or patindex(@m2,replace(s.step_name,'''']'''',''''Â¬''''))>0
    or patindex(@m3,replace(s.step_name,'''']'''',''''Â¬''''))>0
    )
select @rows=@@rowcount
''

