        begin
        raiserror('skipped "%s.%s" because local is more recent',
                  10,1,@db,'sp__printf') with nowait
        goto skip_sp__printf
        end
    end

raiserror('re-creating "%s.%s"',10,1,@db,'sp__printf') with nowait

