    @obj='sp__script_ole',@typ='rv',@ver=@ver out,@aut=@aut out
if not @ver is null
    begin
    if @ver=100328
        begin
        if @aut!='s.zaglio'
            raiserror('local "%s.%s" with same version but different author',
