    '''',N''''@obj sysname,@typ char(2),@ver numeric(10,4) out,@aut sysname out'''',
    @obj=''''%obj%'''',@typ=''''rv'''',@ver=@ver out,@aut=@aut out
if not @ver is null
    begin
    if @ver=%ver%
        begin
        if @aut!=''''%aut%''''
            raiserror(''''local "%s.%s" with same version but different author'''',
                      16,1,@db,''''%obj%'''') with nowait
