begin catch
-- if @@trancount > 0 rollback -- for nested trans see style "procwnt"

exec @ret=sp__err @cod=@proc,@opt=''ex''
return @ret
end catch   -- proc sp__util_os_ps'

end try
begin catch
exec #script_catch 'utility',@repeat
if not error_number() in (208,207)
