-- =================================================================== errors ==
err:        exec @ret=sp__err @e_msg,@proc,@p1=@e_p1,@p2=@e_p2,@p3=@e_p3,
                              @p4=@e_p4,@opt=@e_opt                     goto ret

err_tst:    select @e_msg=''wrong @tst value''                            goto err
-- ===================================================================== help ==
help:
